using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Windows;
using System.Windows.Controls;
using System.Windows.Data;
using System.Windows.Documents;
using System.Windows.Input;
using System.Windows.Media;
using System.Windows.Media.Imaging;
using System.Windows.Shapes;
using TEC.Core.ComponentModel;
using TEC.Core.DirectoryService.ActiveDirectory.ObjectPicker;

namespace TEC.Core.WPF.DirectoryServices.TestApplication.ActiveDirectory
{
    /// <summary>
    /// DirectoryObjectPickerWindow.xaml 的互動邏輯
    /// </summary>
    public partial class DirectoryObjectPickerWindow : Window
    {
        public DirectoryObjectPickerWindow()
        {
            InitializeComponent();
        }

        private void Window_Loaded(object sender, RoutedEventArgs e)
        {
            #region 處理物件類型
            //允許選取物件的類型
            this.AllowObjectTypeList = new List<ObjectTypesCheckBoxItem>();
            //預設選取物件的類型
            this.DefaultObjectTypeList = new List<ObjectTypesCheckBoxItem>();
            foreach (ObjectTypes objectType in Enum.GetValues(typeof(ObjectTypes)))
            {
                if (objectType != ObjectTypes.None && objectType != ObjectTypes.All)
                {
                    this.AllowObjectTypeList.Add(new ObjectTypesCheckBoxItem() { Key = objectType, Value = true });
                    if (objectType == ObjectTypes.Users || objectType == ObjectTypes.Groups
                       || objectType == ObjectTypes.Computers || objectType == ObjectTypes.Contacts)
                    {
                        this.DefaultObjectTypeList.Add(new ObjectTypesCheckBoxItem() { Key = objectType, Value = true });
                    }
                    else
                    {
                        this.DefaultObjectTypeList.Add(new ObjectTypesCheckBoxItem() { Key = objectType, Value = false });
                    }
                }
            }
            this.ListBox_AllowedTypes.ItemsSource = this.AllowObjectTypeList;
            this.ListBox_DefaultTypes.ItemsSource = this.DefaultObjectTypeList;
            #endregion
            #region 處理領域
            //允許選取物件的領域
            this.AllowLocations = new List<LocationsCheckBoxItem>();
            //預設選取物件的領域
            this.DefaultLocations = new List<LocationsCheckBoxItem>();

            foreach (Locations location in Enum.GetValues(typeof(Locations)))
            {
                if (location != Locations.None && location != Locations.All)
                {
                    this.AllowLocations.Add(new LocationsCheckBoxItem() { Key = location, Value = true });
                    if (location == Locations.JoinedDomain)
                    {
                        this.DefaultLocations.Add(new LocationsCheckBoxItem() { Key = location, Value = true });
                    }
                    else
                    {
                        this.DefaultLocations.Add(new LocationsCheckBoxItem() { Key = location, Value = false });
                    }
                }
            }
            this.ListBox_AllowedLocations.ItemsSource = this.AllowLocations;
            this.ListBox_DefaultLocations.ItemsSource = this.DefaultLocations;
            #endregion
        }

        private void Btn_ShowDirectoryObjectSelector_Click(object sender, RoutedEventArgs e)
        {
            try
            {
                ObjectTypes allowedTypes = ObjectTypes.None;
                //篩選已勾選的
                this.AllowObjectTypeList.Where(t => t.Value)
                    .ToList()
                    .ForEach(t => allowedTypes |= t.Key);
                ObjectTypes defaultTypes = ObjectTypes.None;
                //篩選已勾選的
                this.DefaultObjectTypeList.Where(t => t.Value)
                    .ToList()
                    .ForEach(t => defaultTypes |= t.Key);
                Locations allowedLocations = Locations.None;
                //篩選已勾選的
                this.AllowLocations.Where(t => t.Value)
                  .ToList()
                  .ForEach(t => allowedLocations |= t.Key);
                Locations defaultLocations = Locations.None;
                //篩選已勾選的
                this.DefaultLocations.Where(t => t.Value)
                  .ToList()
                  .ForEach(t => defaultLocations |= t.Key);
                // Show dialog
                DirectoryObjectPickerDialog picker = new DirectoryObjectPickerDialog();
                picker.AllowedObjectTypes = allowedTypes;
                picker.DefaultObjectTypes = defaultTypes;
                picker.AllowedLocations = allowedLocations;
                picker.DefaultLocations = defaultLocations;
                picker.MultiSelect = this.CheckBox_MultiSelect.IsChecked.Value;
                picker.TargetComputer = this.Txt_TargetConputer.Text;
                System.Windows.Forms.DialogResult dialogResult = picker.ShowDialog(new Win32Window(this));
                if (dialogResult == System.Windows.Forms.DialogResult.OK)
                {
                    DirectoryObject[] results;
                    results = picker.SelectedObjects;
                    if (results == null)
                    {
                        this.TextBlock_SelectResult.Text = "Results null.";
                        return;
                    }

                    System.Text.StringBuilder sb = new System.Text.StringBuilder();

                    for (int i = 0; i <= results.Length - 1; i++)
                    {
                        sb.Append(string.Format("Name: \t {0}", results[i].Name));
                        sb.Append(Environment.NewLine);
                        sb.Append(string.Format("UPN: \t {0}", results[i].Upn));
                        sb.Append(Environment.NewLine);
                        sb.Append(string.Format("Path: \t {0}", results[i].Path));
                        sb.Append(Environment.NewLine);
                        sb.Append(string.Format("Schema Class: \t {0} ", results[i].SchemaClassName));
                        sb.Append(Environment.NewLine);
                        string downLevelName;
                        try
                        {
                            downLevelName = NameTranslator.TranslateUpnToDownLevel(results[i].Upn);
                        }
                        catch (Exception ex)
                        {
                            downLevelName = string.Format("{0}: {1}", ex.GetType().Name, ex.Message);
                        }
                        sb.Append(string.Format("Down-level Name: \t {0} ", downLevelName));
                        sb.Append(Environment.NewLine);
                        sb.Append(Environment.NewLine);
                    }
                    this.TextBlock_SelectResult.Text = sb.ToString();
                }
                else
                {
                    this.TextBlock_SelectResult.Text = "Dialog result: " + dialogResult.ToString();
                }
            }
            catch (Exception e1)
            {
                MessageBox.Show(e1.ToString());
            }
        }
        /// <summary>
        /// 設定或取得允許選取物件的類型
        /// </summary>
        private List<ObjectTypesCheckBoxItem> AllowObjectTypeList { set; get; }
        /// <summary>
        /// 設定或取得預設選取物件的類型
        /// </summary>
        private List<ObjectTypesCheckBoxItem> DefaultObjectTypeList { set; get; }
        /// <summary>
        /// 設定或取得允許選取物件的區域
        /// </summary>
        private List<LocationsCheckBoxItem> AllowLocations { set; get; }
        /// <summary>
        /// 設定或取得預設選取物件的區域
        /// </summary>
        private List<LocationsCheckBoxItem> DefaultLocations { set; get; }
        #region Private Classes
        private class ObjectTypesCheckBoxItem
        {
            public ObjectTypes Key { set; get; }
            public bool Value { set; get; }
        }
        private class LocationsCheckBoxItem
        {
            public Locations Key { set; get; }
            public bool Value { set; get; }
        }
        #endregion
    }
}

public class WTSUserLoaderTest
{
    public WTSUserLoaderTest()
    {
        this.TimerManager = new TimerManager();//或用現有的排程管理物件
        this.WTSUserCollection = new ThreadSafeObservableCollection<WTSUser>();
        this.WTSUserCollection.CollectionChanged += this.WTSUserCollection_CollectionChanged;
        this.WTSUserCollection.CollectionChanging += this.WTSUserCollection_CollectionChanging;
        this.RefreshWTSUsersTimerStorage = this.TimerManager.createTimersTimer(new RefreshWTSUsersTimerEvent(this.WTSUserCollection, "localhost"),
            new TimePeriodCollection() { new TimeRange(DateTime.Now, DateTime.MaxValue, true) },
            null, 5000, NextTimeEvaluationType.ExecutionEndTime);
        //下行程式可放置相對應的區域執行
        this.RefreshWTSUsersTimerStorage.start();
    }
    private void WTSUserCollection_CollectionChanging(object sender, CollectionChangingEventArgs e)
    {
        //當集合的值準備要發生變更時
    }
    private void WTSUserCollection_CollectionChanged(object sender, System.Collections.Specialized.NotifyCollectionChangedEventArgs e)
    {
        //當工作階段數量發生變更時
    }
    private void WtsUser_PropertyChanged(object sender, PropertyChangedEventArgs e)
    {
        WTSUser wTSUser = sender as WTSUser;
        //當工作階段使用者的屬性值發生變更時
    }
    /// <summary>
    /// 設定或取得目前所有工作階段的資訊
    /// </summary>
    private ThreadSafeObservableCollection<WTSUser> WTSUserCollection { set; get; }
    /// <summary>
    /// 設定或取得用於更新工作階段使用者作業的排程物件
    /// </summary>
    private TimersTimerStorage RefreshWTSUsersTimerStorage { set; get; }
    /// <summary>
    /// 設定或取得排程器管理物件
    /// </summary>
    private TimerManager TimerManager { set; get; }
}
